import React from 'react'
import ReactDOM from 'react-dom'
import {Parallax, ParallaxLayer} from 'react-spring/renderprops-addons'
import {useSpring, animated} from 'react-spring'


import { config } from 'react-spring/renderprops'
import Grid from './Grid'
import { Slug, Fade } from './Primitives'
import data from './data'
import './styles.css'
import 'antd/dist/antd.css'
import { Icon } from 'antd'

class Cell extends Component {
  render() {
    const { toggle, name, description, css, active } = this.props
    return (
      <div
        className="cell"
        style={{ backgroundImage: css, cursor: !active ? 'pointer' : 'auto' }}
        onClick={!active ? toggle : undefined}>
        <Fade show={active} delay={active ? 500 : 0}>
          <div className="details">
            <Slug delay={600}>
              <div className="circle" style={{ background: css }} />
              <div className="close">
                <Icon
                  type="close"
                  style={{ cursor: 'pointer' }}
                  onClick={toggle}
                />
              </div>
              <h1>{name}</h1>
              <p>{description}</p>
            </Slug>
          </div>
        </Fade>
        <Fade
          show={!active}
          from={{ opacity: 0, transform: 'translate3d(0,140px,0)' }}
          enter={{ opacity: 1, transform: 'translate3d(0,0px,0)' }}
          leave={{ opacity: 0, transform: 'translate3d(0,-50px,0)' }}
          delay={active ? 0 : 400}>
          <div className="default">
            <div style={{ zIndex: 1 }}>{name}</div>
          </div>
        </Fade>
      </div>
    )
  }
}
// Little helpers ...
const calc = (x, y) => [x - window.innerWidth / 2, y - window.innerHeight / 2]
const trans1 = (x, y) => `translate3d(${x / 10}px,${y / 10}px,0)`
const trans2 = (x, y) => `translate3d(${x / 8 + 35}px,${y / 8 - 230}px,0)`
const trans3 = (x, y) => `translate3d(${x / 6 - 250}px,${y / 6 - 200}px,0)`
const trans4 = (x, y) => `translate3d(${x / 3.5}px,${y / 3.5}px,0)`
class App extends React.Component {
  render() {
    state = { data }
    return (
    <Parallax pages={3} vertical ref={ref => (this.parallax = ref)}>
      <ParallaxLayer 
      offset={0} 
      speed={0.5} 
      factor={4} 
      style={{  
        backgroundImage: `url("https://images.wallpaperscraft.com/image/stars_starry_sky_night_117616_1280x1024.jpg") ` ,
         backgroundSize: 'contain',
         backgroundPosition:'center',
         backgroundRepeat: 'True'
         }}>
        <span onClick={() => this.parallax.scrollTo(1)}style={{color: '#ffffff'}}>Layers can contain anything</span>
      </ParallaxLayer>

      <ParallaxLayer 
      offset={0} 
      speed={0.2}  
      style={{ backgroundImage: `url("https://images.wallpaperscraft.com/image/astronaut_spacesuit_reflection_144426_1280x1024.jpg")`,
      opacity:0.7,
      backgroundPosition: 'center center',
      backgroundSize:'cover' }}>
        <h2>Hi</h2>
      </ParallaxLayer>

      <ParallaxLayer 
      offset={2} 
      speed={0.2}  
      style={{ backgroundImage: `url("https://images.wallpaperscraft.com/image/man_hoody_hood_graphics_57295_1280x1024.jpg")`,
      opacity:0.7,
      backgroundPosition: 'center center',
      backgroundSize:'cover' }}>
        <h2>Hi</h2>
      </ParallaxLayer>

      <ParallaxLayer 
      offset={1} 
      speed={0.2}  
      style={{ backgroundImage: `url("https://images.wallpaperscraft.com/image/girl_photographer_art_118146_1920x1080.jpg")`,
      opacity:0.5,
      backgroundPosition: 'center center',
      backgroundSize:'cover' }}>
        <h2>Hi</h2>
      </ParallaxLayer>
</Parallax>
    )
  }
}

ReactDOM.render(<App />, document.getElementById('root'))
