import React from 'react'
import ReactDOM from 'react-dom'
import {Parallax, ParallaxLayer} from 'react-spring/renderprops-addons'
import {useSpring, animated} from 'react-spring'


// Little helpers ...
const calc = (x, y) => [x - window.innerWidth / 2, y - window.innerHeight / 2]
const trans1 = (x, y) => `translate3d(${x / 10}px,${y / 10}px,0)`
const trans2 = (x, y) => `translate3d(${x / 8 + 35}px,${y / 8 - 230}px,0)`
const trans3 = (x, y) => `translate3d(${x / 6 - 250}px,${y / 6 - 200}px,0)`
const trans4 = (x, y) => `translate3d(${x / 3.5}px,${y / 3.5}px,0)`
class App extends React.Component {
  render() {
    return (
    <Parallax pages={3} vertical ref={ref => (this.parallax = ref)}>
      <ParallaxLayer offset={0} speed={0.5} factor={3} style={{  backgroundImage: `url("https://images.wallpaperscraft.com/image/hologram_abstraction_blue_141718_3840x2160.jpg") ` , backgroundSize: 'cover'}}>
        <span onClick={() => this.parallax.scrollTo(1)}style={{color: '#ffffff'}}>Layers can contain anything</span>
      </ParallaxLayer>
      <ParallaxLayer offset={1} speed={0.1}  style={{ backgroundImage: `url("https://images.wallpaperscraft.com/image/hologram_pattern_colorful_142690_3840x2400.jpg")`, opacity:0.5, backgroundSize:'cover' }}>
        <h2>Hi</h2>
      </ParallaxLayer>
      <ParallaxLayer
          offset={2}
          speed={-0.3}
          style={{
            backgroundSize: '80%',
            backgroundPosition: 'center',
            backgroundImage: `url("https://images.wallpaperscraft.com/image/hologram_pattern_colorful_142690_3840x2400.jpg")`
          }}
        />
</Parallax>
    )
  }
}

ReactDOM.render(<App />, document.getElementById('root'))
